import "./Order.css"
import {useState} from "react";
import { getItem } from "../../requests";
import plus from "../../Assets/plus.svg"
function Order() {
    const [name, setName] = useState();
    const [address, setAddress] = useState();
    const [menuItem, setMenuItem] = useState();
    const [quantity, setQuantity] = useState();
    const [search, setSearch] = useState("");
    const [returnedData, setReturnedData] = useState([]);
    const handleSubmit = () => {

    }

    const searchHandler = async (e) => {
        e.preventDefault();
        const { data } = await getItem(search);
        setReturnedData(data);
    }

    return(
        <main>
            <div className="container_order">
                <h2>Złóż zamówienia</h2>
                <div className="search_cont">
                    <form onSubmit={searchHandler}>
                        <label htmlFor="search">Wyszukaj produktu:</label>
                        <input type="text" name="search" id="search" value={search} onChange={(e) => {setSearch(e.target.value)}} required />
                        <button>Wyszukaj</button>
                    </form>
                </div>
                <div className="search_data">
                    {returnedData.map(item => (
                        <div key={item._id}>
                            <p>{item.nazwa}</p>
                            <img className="add_icon" src={plus} alt="Dodaj"/>
                        </div>
                    ))}
                </div>
                <div>
                    <h2>Koszyk</h2>
                </div>
                <form onSubmit={handleSubmit} className="order-form">
                    <label htmlFor="name">Imię:</label>
                    <input
                        type="text"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />

                    <label htmlFor="address">Adres:</label>
                    <input
                        type="text"
                        id="address"
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                        required
                    />

                    <label htmlFor="menuItem">Pozycja menu:</label>
                    <select
                        id="menuItem"
                        value={menuItem}
                        onChange={(e) => setMenuItem(e.target.value)}
                        required
                    >
                        <option value="">Wybierz...</option>
                        <option value="pizza">Pizza</option>
                        <option value="pasta">Pasta</option>
                        <option value="salad">Sałatka</option>
                    </select>

                    <label htmlFor="quantity">Ilość:</label>
                    <input
                        type="number"
                        id="quantity"
                        value={quantity}
                        onChange={(e) => setQuantity(e.target.value)}
                        min="1"
                        required
                    />
                    <button type="submit">Złóż zamówienie</button>
                </form>
            </div>
        </main>
    )
}

export default Order;
