import { useLocation } from "react-router-dom";
import {useEffect, useState} from "react";
import "./Nav.css"
import bkg from "../../Assets/mainPage.jpg"
function Nav() {
    const location = useLocation();
    const [ headerImage, setHeaderImage] = useState('');
    const getImagePath = () => {
        switch (location.pathname) {
            case '/':
                return process.env.PUBLIC_URL + '/home-image.jpg';
            case '/about':
                return process.env.PUBLIC_URL + '/about-image.jpg';
            case '/contact':
                return process.env.PUBLIC_URL + '/contact-image.jpg';
            default:
                return process.env.PUBLIC_URL + '/default-image.jpg';
        }
    }
    useEffect(() => {
            setHeaderImage(getImagePath());
    }, [location]);

    return(
        <header>
            <div className="headerImg" style={{ backgroundImage: `url(${headerImage})`}}>
                afas
            </div>
            <nav>
                <ul className="nav__items">
                    <li>O nas</li>
                    <li>Nasze Menu</li>
                    <li>Książki</li>
                    <li>Galeria</li>
                    <li>Kontakt</li>
                </ul>
            </nav>

        </header>
    )
}

export default Nav;
