import axios from "axios";

export const getMenu = async () => {
    return axios.get("http://localhost:3001")
}

export const getItem = async (val) => {
    try {
        axios.post("http://localhost:3001",{val})
    } catch (err) {
        return "Nie mamy tego w menu"
    }
}
